import 'package:flutter/material.dart';class AttachmentOptions extends StatelessWidget {  final VoidCallback onSelectImage;  final VoidCallback onSelectFile;  const AttachmentOptions({    super.key,    required this.onSelectImage,    required this.onSelectFile,  });  @override  Widget build(BuildContext context) {    return Container(      padding: const EdgeInsets.all(16),      decoration: BoxDecoration(        color: Colors.white,        borderRadius: const BorderRadius.only(          topLeft: Radius.circular(16),          topRight: Radius.circular(16),        ),        boxShadow: [          BoxShadow(            color: Colors.black.withOpacity(0.1),            spreadRadius: 5,            blurRadius: 7,            offset: const Offset(0, 3),          ),        ],      ),      child: Wrap(        children: [          ListTile(            leading: const Icon(Icons.image, color: Colors.blue),            title: const Text('Select Image'),            onTap: onSelectImage,          ),          ListTile(            leading: const Icon(Icons.attach_file, color: Colors.green),            title: const Text('Select File'),            onTap: onSelectFile,          ),        ],      ),    );  }}