import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:webitel_sdk/presentation/bloc/auth/auth_bloc.dart';import 'package:webitel_sdk/presentation/bloc/chat/chat_bloc.dart';import 'package:webitel_sdk/presentation/widget/chat_input.dart';import 'package:webitel_sdk/presentation/widget/messages_listview.dart';class ChatBody extends StatelessWidget {  final ChatBloc chatBloc;  final AuthBloc authBloc;  const ChatBody({    required this.chatBloc,    required this.authBloc,    super.key,  });  @override  Widget build(BuildContext context) {    final ScrollController scrollController = ScrollController();    return ListView(      controller: scrollController,      keyboardDismissBehavior: ScrollViewKeyboardDismissBehavior.onDrag,      children: [        SizedBox(          height: MediaQuery.of(context).size.height * 0.83,          child: Scrollbar(            controller: scrollController,            trackVisibility: true,            thickness: 2,            thumbVisibility: true,            child: MessagesListView(              chatBloc: chatBloc,              authBloc: authBloc,            ),          ),        ),        const Divider(thickness: 2),        Padding(          padding: const EdgeInsets.symmetric(horizontal: 16),          child: BlocBuilder<ChatBloc, ChatState>(            bloc: chatBloc,            builder: (context, state) {              return ChatInput(chatBloc: chatBloc);            },          ),        ),      ],    );  }}